const state = {
  loading: true,
  success: false,
  data: [],
};

const tableDataReducer = (state, action) => {
  switch (action.type) {
    case "LOAD_DATA":
      state.loading = false;
      state.fail = false;
      state.success = false;
      return state;
    case "LOADING":
      state.loading = true;
      return state;
    case "FAIL":
      state.success = false;
      return state;
    case "SUCCESSFUL":
      state.success = true;
      return state;
    default:
      return state;
  }
};
export default tableDataReducer;
