import produce from "immer";

const initState = {
  loading: true,
  success: false,
  data: [],
};

const tableDataReducer = (state = initState, action) =>
  produce(state, (draft) => {
    switch (action.type) {
      case "LOAD_DATA":
        draft.loading = false;
        draft.fail = false;
        draft.success = false;
        break;
      case "LOADING":
        draft.loading = true;
        break;
      case "FAIL":
        draft.success = false;
        break;
      case "SUCCESSFUL":
        draft.success = true;
        break;
      default:
        return draft;
    }
    return draft;
  });
export default tableDataReducer;
